select * from customer limit 20


--Q1 what is the total revenue generated by male vs female customers
select gender, sum(purchase_amount) as revenue
from customer
group by gender


--Q2 which customers used a discount but still spent more than the average purchase amount ?
select customer_id, purchase_amount
from customer
where discount_applied = 'Yes' and purchase_amount >= (select avg(purchase_amount) from customer)


--Q3 which are top 5 products with the highest average review rating?
select item_purchased, round(avg(review_rating::numeric),2) as average_product_rating
from customer
group by item_purchased
order by avg(review_rating) desc
limit 5;


--Q4 compare the average purchase amount between standard and express shipping?
select shipping_type, round(avg(purchase_amount),2)
from customer
where shipping_type in ('Standard','Express')
group by shipping_type


--Q5 do subscribed customers spend more, compare average spent and total revenue between subscribers and non-subscribers
select subscription_status,count(customer_id), round(avg(purchase_amount),2) as avg_spent, round(sum(purchase_amount),2) as total_rev
from customer
group by subscription_status



--Q6 which top 5 products have the highest percentage of purchases with discount applied ?
select item_purchased, 
round(100 * sum(case when discount_applied = 'Yes' then 1 else 0 end)/count(*),2) as discount_percentage
from customer
group by item_purchased
order by discount_percentage desc
limit 5;


--Q7 Segment customers into New, Returning and Loyal based on their total number of previous purchase and show the count of each segment
SELECT customer_segment, COUNT(*)
FROM (
  SELECT 
    customer_id, 
    previous_purchases,
    CASE
      WHEN previous_purchases = 1 THEN 'New'
      WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
      ELSE 'Loyal'
    END AS customer_segment
  FROM customer
) AS customer_type
GROUP BY customer_segment;


--Q8 what are the top 3 most purchased products in each category
select top_rank, category, item_purchased, total_orders 
from
(select category, item_purchased, count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id) desc) as top_rank
from customer
group by category, item_purchased) as item_count
where top_rank<=3


--Q9 Are customers who are repeat buyers(more than 5 pervious purchases) also likely to subscribe ?
select subscription_status, count(customer_id) as repeat_buyers
from customer
where previous_purchases >= 5
group by subscription_status


--Q10 what is the revenue contribution of each age group
select age_group, sum(purchase_amount) as revenue
from customer
group by age_group
order by revenue desc